// Get all existing events for the day
const events = $input.all().map(item => item.json);

// Business hours: 9 AM to 5 PM, 30-min slots
const slots = [];
const bookedTimes = events.map(e => {
  const start = new Date(e.start?.dateTime || e.start?.date);
  const end = new Date(e.end?.dateTime || e.end?.date);
  return { start, end };
});

const requestDate = $('Webhook - Check Availability').item.json.body.date;

for (let hour = 9; hour < 17; hour++) {
  for (let min = 0; min < 60; min += 30) {
    const slotStart = new Date(`${requestDate}T${String(hour).padStart(2,'0')}:${String(min).padStart(2,'0')}:00`);
    const slotEnd = new Date(slotStart.getTime() + 30 * 60000);

    const isBooked = bookedTimes.some(b => 
      slotStart < b.end && slotEnd > b.start
    );

    if (!isBooked) {
      slots.push(`${String(hour).padStart(2,'0')}:${String(min).padStart(2,'0')}`);
    }
  }
}

const dateFormatted = new Date(requestDate).toLocaleDateString('en-US', { weekday: 'long', month: 'long', day: 'numeric' });

let message;
if (slots.length === 0) {
  message = `There are no available slots on ${dateFormatted}. Would you like to check another date?`;
} else {
  const slotList = slots.map(s => {
    const [h, m] = s.split(':');
    const hour12 = h % 12 || 12;
    const ampm = h < 12 ? 'AM' : 'PM';
    return `${hour12}:${m} ${ampm}`;
  }).join(', ');
  message = `Available times on ${dateFormatted}: ${slotList}. Which time works best for you?`;
}

return [{ json: { result: message, available_slots: slots } }];
